INSERT INTO `resort` VALUES ('1', '妙峰山', '', 'AAA', '妙峰山风景名胜区位于北京门头沟区境内，海拔1291米，距市中心50余公里。有“华北第一仙山”之称。\n妙峰山旧名仰山，以古刹、奇松、怪石而闻名。山上林木葱茏，风景优美，古多庙宇，有日出、晚霞、雾凇、山市等时令景观，有戴“华北一绝”桂冠的千亩玫瑰花。妙峰山庙会非常有名，其规模堪称华北之首。另外创建于明末的“天仙圣母碧霞元君庙”也很著名。妙峰山是北京周边最具文化底蕴的风景名胜区之一。', '北京', '北京 门头沟区妙峰山镇', '61882936 61882937', '', '40.07398502', '116.0371922', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/8601a18b87d6277f7613299328381f30e924fc1c.jpg', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/18d8bc3eb13533fad62efd80a8d3fd1f40345b87.jpg', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/c8177f3e6709c93d136a60599f3df8dcd0005487.jpg', '');
