INSERT INTO `resort` VALUES ('133', '北京钓鱼台国宾馆', '', '', '钓鱼台国宾馆坐落于北京海淀区玉渊潭东侧，是中国古代皇家园林及现代国宾馆建筑群。金代章宗皇帝完颜璟曾在此筑台垂钓，“钓鱼台”因而得名，迄今已有800余年。', '北京', '海淀区阜成路2号(近三里河路)', '', '', '39.92514905', '116.3395789', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/6c224f4a20a446231a32b79d9922720e0df3d750.jpg', '', '', '');
