INSERT INTO `resort` VALUES ('211', '京西十八潭', '', 'AAA', '京西十八潭位于门头沟区王平地区办事处安家庄村境内，地处海拔1528米的清水尖山北麓。\n京西十八潭以潭而闻名，溪水四季长流，泉水清浏甘甜。十八个潭随山势布于峡谷之中，潭潭相连，大小、深浅、形态各不相同，但潭潭有水，潭潭有鱼，妙趣天成。峡谷中有三处自然形成的瀑布，峡谷尽头的一处瀑布落差三丈有余，如雪链般急泻而下，又好似巨龙吐珠，雪花四溅，凉爽宜人，实是消夏避暑的好去处。', '北京', '门头沟区安家庄村', '', '', '40.02029604', '115.9317899', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/4610b912c8fcc3ce5a79cc659345d688d43f202e.jpg', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/d058ccbf6c81800a635c8c58b03533fa828b472e.jpg', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/f2deb48f8c5494ee939b0a4f2cf5e0fe99257e2e.jpg', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/86d6277f9e2f0708cf9c2f56e824b899a801f2cf.jpg');
