INSERT INTO `resort` VALUES ('474', '钓鱼台银杏大道', '', '', '钓鱼台银杏林位于西城区三里河路钓鱼台国宾馆东墙外，现在已成为北京观赏银杏的一个地标，是京城人赏秋，思秋，怀秋，恋秋玩耍的好去处。\n北京地区的银杏种植历史比较久远，以钓鱼台银杏大道最美最有名，据说这里的银杏是北京最早种下的一批，比起日坛里的更加密集粗壮。\n每到秋冬交替的好时节，大大小小的银杏树满树金黄将这条街装点得素气大方，树下的芳草地被厚厚的金色的银杏树叶覆盖，踩起来“沙沙”作响。笔直的银杏大道里面是满满的人流，欣赏杏叶的老人孩子，拍婚纱照的浪漫情侣，举着长枪短炮的“拍客”，支起画板的“画家”，都是林中一景。', '北京', '西城区三里河路钓鱼台国宾馆东墙外(钓鱼台国宾馆东侧路)', '', '', '39.91989122', '116.3408725', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/5d6034a85edf8db160f6b8440b23dd54564e7432.jpg', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/eac4b74543a98226c4cdec078882b9014a90eb86.jpg', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/b21c8701a18b87d6051c6f3f050828381f30fdb0.jpg', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/b21bb051f81986182c872aa448ed2e738ad4e6ca.jpg');
