INSERT INTO `resort` VALUES ('497', '中华世纪坛', '', 'AAA', '一座日晷型的纪念性建筑，坐落在北京西长安街延长线上，在中国革命军事博物馆西侧，由主体结构、青铜甬道、圣火广场、过街桥、世纪大厅、艺术大厅等组成。\n中华世纪坛是为了迎接21世纪新千年而兴建的。从中华世纪坛到它所在的街道尽头共270米，表现了历代帝王的九五之尊。南面入口处伫立着一块长9米、高1.05米、重34.6吨的汉白玉题字碑，上面刻着国家主席江泽民的题词“中华世纪坛”。背面为“中华世纪坛序”。据悉，这是世界上最大的一块汉白玉。', '北京', '北京市海淀区复兴路甲9号', '010-59802222', 'http://www.worldartmuseum.cn/ind', '39.91514834', '116.3281497', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/86d6277f9e2f07080dc1ecc0e924b899a801f22e.jpg', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/00e93901213fb80ef1fca01736d12f2eb83894c5.jpg', '', '');
