INSERT INTO `resort` VALUES ('1360', '雍王府', '', '', '雍王府就是明朝英宗第八子雍靖王墓，在西小府和申王府之间。明代的王爷坟以“府”相称。石景山区八大处附近的雍王府，就是明代诸王爷坟之一。雍王府因雍靖王而得名。《明史》说雍靖王是宪宗第八子。雍王府出土的《雍靖王圹志》却说雍靖王是“宪宗皇帝第六子”。这是雍靖王身世之谜。', '北京', '', '', '', '39.95236789', '116.1865786', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/d50735fae6cd7b89b4f5b6600e2442a7d8330eb3.jpg', '', '', '');
