INSERT INTO `resort` VALUES ('1373', '上庄水库', '', '', '坐落在北京西北郊，为于北京市海淀区上庄镇，紧邻国家翠湖湿地公园，上庄水库有众多烧烤，是集郊游、踏青、烧烤、垂钓为一体。距离城区30公里。上庄水库是由旧河道筑拦水而成的，全长约4公里左右，库区弯弯曲曲，宽窄不一。上庄水库与稻香湖本是一桥之隔的同一水道，水体相连。由于上游湖段水较浅，下游库段水较深，因而常年有鱼从上游艺机来到库区窝居繁衍，鱼的密度较其他大型水库自然也大些。库中鱼种类繁多，有鲫、鲤、草、鲢、鳙、鲶、黑和鳜鱼等，其中最大的鲤鱼可达5公斤以上，500克左右的鲫鱼也不少见。', '北京', '海淀区上庄镇上庄乡上庄村(近翠湖湿地公园)', '01082473098', '', '40.11165706', '116.2108720', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/d01373f082025aaf01520136faedab64024f1aee.jpg', '', '', '');
