INSERT INTO `resort` VALUES ('1501', '张裕爱斐堡', '', '--', '北京张裕爱斐堡国际酒庄有限公司位于北京市密云县巨各庄镇，距市中心88公里。是由烟台张裕集团融合法国、美国、意大利、葡萄牙等多国资本打造完成。 \n爱斐堡配备了葡萄酒主题旅游、专业品鉴培训、休闲度假三大创新功能，开启了世界酒庄新时代。 \n爱斐堡呈欧式风格，法桐大道，鲜食葡萄采摘园，哥特式城堡，地下大酒窖，欧洲小镇，张裕百年历史博物馆以及山水景观休闲区，使您可以尽情体验葡萄酒文化所带来的乐趣，与大自然亲密接触，彻底地放松身心。', '北京', '北京市密云县宁菜路京承高速密云城东收费站旁', '010-57255509', '', '40.38187304', '116.9082729', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/7aec54e736d12f2effb4a06e4ec2d5628535680e.jpg', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/8d5494eef01f3a29775ddbc09825bc315c607c22.jpg', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/6f061d950a7b02089d5331ef63d9f2d3572cc822.jpg', '');
