INSERT INTO `resort` VALUES ('1508', '周口店北京人遗址', 'Peking Man Site at Zhoukoudian', 'AAAA', '周口店遗址博物馆坐落在北京城西南房山区周口店龙骨山脚下，距北京市区约48公里。是世界上材料最丰富、最系统、最有价值的旧石器时代早期的人类遗址。\n周口店北京人遗址博物馆分四个展厅，系统介绍60万年前的“北京人”、10万年前的“新洞人”、18000年前的“山顶洞人”的生活状况。周口店保存了纵贯70万年的人类生存历史。\n周口店遗址自1927年进行大规模系统发掘以来，共发现不同时期的各类化石和文化遗物地点27处，发掘出土代表40多个“北京人”的化石遗骸，10多万件石器，近200种动物化石及大量的用火遗迹等。', '北京', '北京市房山区周口店大街1号', '010-69301287、69301', 'http://www.zkd.cn/', '39.69664109', '115.9351050', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/94cad1c8a786c917b46397f9c93d70cf3ac7578a.jpg', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/2cf5e0fe9925bc31f63dc4585edf8db1ca13708a.jpg', '', '');
