INSERT INTO `resort` VALUES ('1528', '汉石桥湿地公园', '', '', '北京汉石桥湿地公园自然风景区位于北京市东北部的顺义杨镇地区，该自然风景区距北京城区约35公里，总面积1900公顷。\n这是北京市唯一现存的大型芦苇沼泽湿地以及多种珍稀水禽的栖息地，其中国家Ⅰ级重点保护野生动物2种，国家Ⅱ级重点野生动物17种，这里大面积生长的芦苇，在北京近郊地区更是绝无仅有的，成为汉石桥湿地的标志性特征。\n由此赢得了“京东大芦荡”、京郊“小白洋淀”的别称，具有极大的保护和科研价值。', '北京', '北京市顺义区杨镇', '01061411200', 'http://www.hsq.bjshy.gov.cn/', '40.12993306', '116.8175409', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/4b90f603738da977d10cad69b151f8198618e355.jpg', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/c8ea15ce36d3d5398d1f24813b87e950352ab055.jpg', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/960a304e251f95ca48f2b9f4c8177f3e67095268.jpg', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/962bd40735fae6cdbf9680230eb30f2442a70f55.jpg');
INSERT INTO `resort` VALUES ('1529', '京西古道', '', '', '京西古道是京西古代文明的重要标志和历史见证。\n京西之山，统称西山。元、明以来，京城百万人家，皆以石炭为薪。加之这里出产石材，琉璃的烧制更是闻名京城，于是，拉煤运货的驼马成群结队，日复一日地在山路石道上来回，久而久之便形成了京城到西部山区、再远至内蒙古、山西等地的商旅道路。', '北京', '近郊门头沟妙峰山镇水峪嘴村(近马致远故居)', '', '', '39.97239510', '116.0548019', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/f7246b600c338744ee835fff510fd9f9d62aa0ea.jpg', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/e61190ef76c6a7ef9943a980fcfaaf51f3de662a.jpg', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/ac6eddc451da81cb9d83826b5366d0160924312a.jpg', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/86d6277f9e2f0708fb4c5a83e924b899a801f2ec.jpg');
