INSERT INTO `resort` VALUES ('1556', '拒马乐园', '', 'AAAA', '拒马乐园位于十渡风景名胜区内，河宽水清，山势险峻，以“幽、古、秀、谜、惊、险、奇、特”的美景享誉中华，是十渡自然风景区景色最好、娱乐项目最集中、游客最多、品位最高的景区。\n拒马乐园第一个将勇敢者的游戏——蹦极跳引入中国，落户十渡，引发了全国范围内的蹦极热，被誉为“十渡蹦极，神州第一”，尤其是一处双台蹦极，更是世界第一。\n乐园横跨八渡的麒麟山与拒马河之间，山水环抱，现已形成集山上、山下、水上、空中于一体的大型综合游乐园，主要有碧莹水上游乐场、石佛馆、索道、甭急、峡谷飞人、滑翔飞翼等景区景点。', '北京', '近郊房山区涞宝路十渡自然风景区', '', '', '39.64074312', '115.6012070', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/78310a55b319ebc4f2bbf57f8326cffc1f171610.jpg', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/622762d0f703918fd711be2c503d269758eec410.jpg', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/54fbb2fb43166d22cea89672472309f79152d27d.jpg', '//gss0.baidu.com/6b1IcTe9R1gBo1vgoIiO_jowehsv/maps/services/thumbnails?width=525&height=295&quality=100&align=middle,middle&src=http://gss0.baidu.com/7LsWdDW5_xN3otqbppnN2DJv/lvpics/pic/item/a6efce1b9d16fdfa77ac4508b58f8c5495ee7b10.jpg');
